<script setup lang="ts">
import { useMoviesStore } from '~/stores/movies'
import axios from 'axios'
import { ref, computed, onMounted, onUnmounted } from 'vue'

const moviesStore = useMoviesStore()
const headerMovie = ref(null)
const currentPage = ref(1)
const moviesPerPage = 16
const loadingMore = ref(false)
const showScrollTop = ref(false)

const displayedMovies = computed(() => {
  return moviesStore.trendingMovies.slice(0, currentPage.value * moviesPerPage)
})

function scrollToTop() {
  window.scrollTo({
    top: 0,
    behavior: 'smooth'
  })
}

function handleScroll() {
  showScrollTop.value = window.scrollY > 500
}

async function loadMore() {
  loadingMore.value = true
  currentPage.value++
  
  // Check if we need to fetch more movies from the API
  if (displayedMovies.value.length >= moviesStore.trendingMovies.length) {
    await moviesStore.fetchTrendingPage(Math.ceil(displayedMovies.value.length / 20) + 1)
  }
  
  loadingMore.value = false
}

// Get a random classic movie for the header
async function fetchRandomClassicMovie() {
  try {
    // Get a random page between 1-10 of classic movies from TMDB
    const randomPage = Math.floor(Math.random() * 10) + 1
    
    // Use a year range for classic movies (e.g., before 1990)
    const response = await axios.get('https://api.themoviedb.org/3/discover/movie', {
      params: {
        api_key: '262d5ff6a75ab6f20cf8594e41b2ac86',
        language: 'en-US',
        sort_by: 'popularity.desc',
        include_adult: false,
        page: randomPage,
        'primary_release_date.lte': '1990-12-31',
        'vote_count.gte': 100,
        with_backdrop_path: true
      }
    })
    
    if (response.data.results && response.data.results.length > 0) {
      // Select a random movie from the results
      const randomIndex = Math.floor(Math.random() * response.data.results.length)
      headerMovie.value = response.data.results[randomIndex]
    }
  } catch (error) {
    console.error('Error fetching random classic movie:', error)
  }
}

onMounted(async () => {
  // Fetch the header image
  await fetchRandomClassicMovie()
  
  // Fetch movies if not already loaded
  if (moviesStore.trendingMovies.length === 0) {
    await moviesStore.fetchTrending()
  }

  // Add scroll event listener
  window.addEventListener('scroll', handleScroll)
})

onUnmounted(() => {
  // Remove scroll event listener when component is destroyed
  window.removeEventListener('scroll', handleScroll)
})
</script>

